import React, { useEffect, useState } from 'react';
import Card from '../Components/Card';
import axios from 'axios'; // Agrega esta línea

const Home = () => {
  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    axios.get('https://jsonplaceholder.typicode.com/users')
      .then((response) => setDentists(response.data))
      .catch((error) => console.error('Error al obtener datos de los dentistas', error));
  }, []);

  const addToFavorites = (id) => {
    // Recupera los favoritos actuales del localStorage (si los hay)
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];

    // Verifica si el dentista ya está en favoritos
    if (!favorites.includes(id)) {
      // Si no está en favoritos, agrégalo
      favorites.push(id);

      // Actualiza el localStorage con los nuevos favoritos
      localStorage.setItem('favorites', JSON.stringify(favorites));
    }
  };

  return (
    <main className="light">
      <h1>Home</h1>
      <div className="card-grid">
        {dentists.map((dentist) => (
          <Card
            key={dentist.id}
            dentist={dentist}
            addToFavorites={addToFavorites}
          />
        ))}
      </div>
    </main>
  );
};

export default Home;
