import React, { useEffect, useState } from 'react';
import Card from '../Components/Card';

const Favs = () => {
  const [selectedDentist, setSelectedDentist] = useState(null);

  useEffect(() => {
    // Recupera el dentista seleccionado del Local Storage
    const storedSelectedDentist = localStorage.getItem('selectedDentist');

    if (storedSelectedDentist) {
      // Si hay un dentista seleccionado, muestra y parsea los datos
      const parsedSelectedDentist = JSON.parse(storedSelectedDentist);
      setSelectedDentist(parsedSelectedDentist);

      // Limpia el dentista seleccionado del Local Storage
      localStorage.removeItem('selectedDentist');
    }
  }, []);

  return (
    <>
      <h1>Dentists Favs</h1>
      {selectedDentist ? (
        <div className="card-grid">
          <Card
            key={selectedDentist.id}
            dentist={selectedDentist}
          />
        </div>
      ) : (
        <p>No tienes dentistas destacados.</p>
      )}
    </>
  );
};

export default Favs;
