import React, { useEffect, useState } from 'react';
import Card from '../Components/Card';

const Home = () => {
  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    // Simulación de carga de datos desde una API (reemplaza con tu fuente de datos real)
    const fetchData = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        setDentists(data);
      } catch (error) {
        console.error('Error al obtener datos de los dentistas', error);
      }
    };

    fetchData();
  }, []);

  const addToFavorites = (id) => {
    // Recupera los favoritos actuales del localStorage (si los hay)
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];

    // Verifica si el dentista ya está en favoritos
    if (!favorites.includes(id)) {
      // Si no está en favoritos, agrégalo
      favorites.push(id);

      // Actualiza el localStorage con los nuevos favoritos como un arreglo JSON válido
      localStorage.setItem('favorites', JSON.stringify(favorites));
    }
  };

  return (
    <main className="light">
      <h1>Home</h1>
      <div className="card-grid">
        {dentists.map((dentist) => (
          <Card
            key={dentist.id}
            dentist={dentist}
            addToFavorites={() => addToFavorites(dentist.id)}
          />
        ))}
      </div>
    </main>
  );
};

export default Home;
