import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import '../index.css';
import { GlobalContext } from './utils/global.context'; // Importa el contexto global

const Navbar = () => {
  const { state, updateTheme } = useContext(GlobalContext); // Obtén el tema del contexto y la función para actualizarlo

  const toggleTheme = () => {
    const newTheme = state.theme === 'light' ? 'dark' : 'light';
    updateTheme(newTheme); // Actualiza el tema utilizando la función del contexto
  };

  const theme = state.theme;

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem',
    background: theme === 'light' ? 'white' : '#12121296',
    color: theme === 'light' ? 'black' : 'white',
  };

  const ulStyle = {
    listStyle: 'none',
    margin: 0,
    padding: 0,
    display: 'flex',
  };

  const liStyle = {
    marginRight: '20px',
  };

  return (
    <nav style={navbarStyle} className={theme}>
      <ul style={ulStyle}>
        <li style={liStyle}>
          <Link to="/home">Home</Link>
        </li>
        <li style={liStyle}>
          <Link to="/contacto">Contacto</Link>
        </li>
        <li style={liStyle}>
          <Link to="/favs">Favoritos</Link>
        </li>
      </ul>
      <button onClick={toggleTheme}>Cambiar Tema</button>
    </nav>
  );
};

export default Navbar;
