import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './Components/Navbar';
import Footer from './Components/Footer';
import Home from './Routes/Home';
import Contacto from './Routes/Contact';
import Favoritos from './Routes/Favs';
import Detail from './Routes/Detail';
import './index.css';
import { GlobalContext } from './Components/utils/global.context';

function App() {
  const { state } = useContext(GlobalContext);
  const theme = state.theme;

  const appStyle = {
    background: theme === 'light' ? 'var(--primary-bg-color)' : '#12121296',
    color: theme === 'light' ? 'var(--primary-text-color)' : 'white',
    minHeight: '100vh', // Asegura que la página tenga al menos la altura de la ventana
  };

  return (
    <GlobalContextProvider>
      <Router>
        <div className="App" style={appStyle}>
          <Navbar />
          <Routes>
            <Route path="/home" element={<Home />} />
            <Route path="/contacto" element={<Contacto />} />
            <Route path="/favs" element={<Favoritos />} />
            <Route path="/dentist/:id" element={<Detail />} />
          </Routes>
          <Footer />
        </div>
      </Router>
    </GlobalContextProvider>
  );
}

export default App;
