import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './Components/Navbar';
import Footer from './Components/Footer';
import Home from './Routes/Home';
import Contacto from './Routes/Contact';
import Favoritos from './Routes/Favs';
import Detail from './Routes/Detail';
import './index.css';
import { GlobalContext } from './Components/utils/global.context'; // Importa el contexto global

function App() {
  const { state } = useContext(GlobalContext); // Obtén el estado global

  return (
    <Router>
      <div className={`App ${state.theme}`}> {/* Aplica el tema del contexto */}
        <Navbar />
        <Routes>
          <Route path="/home" element={<Home />} />
          <Route path="/contacto" element={<Contacto />} />
          <Route path="/favs" element={<Favoritos />} />
          <Route path="/dentist/:id" element={<Detail />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
