import React, { useEffect, useState } from 'react';
import Card from '../Components/Card';

const Favs = () => {
  const [favorites, setFavorites] = useState([]);
  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    // Recupera los dentistas destacados del localStorage
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    console.log('Favoritos recuperados del localStorage:', storedFavorites);
    setFavorites(storedFavorites);

    // Recupera todos los dentistas (puedes obtenerlos de tu fuente de datos)
    // Por ahora, usaremos un array vacío ya que los datos provienen de la página de inicio
    const exampleDentists = [];
    setDentists(exampleDentists);
  }, []);

  // Filtra los dentistas favoritos y muestra solo esos
  const favoriteDentists = dentists.filter((dentist) => favorites.includes(dentist.id));

  return (
    <>
      <h1>Dentists Favs</h1>
      {favorites.length === 0 ? (
        <p>No tienes dentistas destacados.</p>
      ) : (
        <div className="card-grid">
          {favoriteDentists.map((dentist) => (
            <Card
              key={dentist.id}
              dentist={dentist}
            />
          ))}
        </div>
      )}
    </>
  );
};

export default Favs;
