import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './Components/Navbar';
import Footer from './Components/Footer';
import Home from './Routes/Home';
import Contacto from './Routes/Contact';
import Favoritos from './Routes/Favs';
import Detail from './Routes/Detail'; // Importa el componente Detail
import './index.css';

// Importa el contexto de tema
import { useContext } from 'react';
import { ThemeContext } from './App'; // Asegúrate de que la importación sea correcta

function App() {
  // Obtiene el valor del tema del contexto
  const theme = useContext(ThemeContext);

  return (
    <Router>
      <div className={`App ${theme}`}>
        <Navbar />
        <Routes>
          <Route path="/home" element={<Home />} />
          <Route path="/contacto" element={<Contacto />} />
          <Route path="/favs" element={<Favoritos />} />
          <Route path="/dentist/:id" element={<Detail />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
