import React, { useEffect, useState } from 'react';
import Card from '../Components/Card';

const Favs = () => {
  const [favorites, setFavorites] = useState([]);
  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    // Recupera los dentistas destacados del localStorage
    const storedFavorites = localStorage.getItem('favorites');
    if (storedFavorites) {
      try {
        // Intenta parsear los datos del Local Storage a un arreglo
        const parsedFavorites = JSON.parse(storedFavorites);
        setFavorites(parsedFavorites);
      } catch (error) {
        console.error('Error al parsear los favoritos del localStorage:', error);
      }
    } else {
      // Si no hay datos en el Local Storage, establece favorites como un arreglo vacío
      setFavorites([]);
    }

    // Obtener los dentistas de la API
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((response) => response.json())
      .then((data) => {
        // Establecer los dentistas obtenidos de la API
        setDentists(data);
      })
      .catch((error) => {
        console.error('Error al obtener datos de los dentistas desde la API:', error);
      });
  }, []);

  // Filtra los dentistas favoritos y muestra solo esos
  const favoriteDentists = dentists.filter((dentist) => favorites.includes(dentist.id));

  return (
    <>
      <h1>Dentists Favs</h1>
      {favorites.length === 0 ? (
        <p>No tienes dentistas destacados.</p>
      ) : (
        <div className="card-grid">
          {favoriteDentists.map((dentist) => (
            <Card
              key={dentist.id}
              dentist={dentist}
            />
          ))}
        </div>
      )}
    </>
  );
};

export default Favs;
