import React, { useEffect, useState } from 'react';
import Card from '../Components/Card';

const Favs = () => {
  const [favoriteDentists, setFavoriteDentists] = useState([]);
  useEffect(() => {
    // Recupera la lista de dentistas favoritos del Local Storage
    const storedFavoriteDentists = JSON.parse(localStorage.getItem('favoriteDentists')) || [];
    setFavoriteDentists(storedFavoriteDentists);
  }, []);


  const removeFavoriteDentist = (dentistId) => {
    // Remover el dentista favorito por su ID
    const updatedFavorites = favoriteDentists.filter((dentist) => dentist.id !== dentistId);
    setFavoriteDentists(updatedFavorites);
    // Actualizar el Local Storage con la lista actualizada
    localStorage.setItem('favoriteDentists', JSON.stringify(updatedFavorites));
  };

  return (
    <>
      <h1>Dentistas Favs</h1>
      {favoriteDentists.length > 0 ? (
        <div className="card-grid">
          {favoriteDentists.map((dentist) => (
            <div key={dentist.id} className="card">
              <h2>{dentist.name}</h2>
              <p>Username: {dentist.username}</p>
              <button onClick={() => removeFavoriteDentist(dentist.id)}>Eliminar</button>
            </div>
          ))}
        </div>
      ) : (
        <p>No tienes dentistas favoritos.</p>
      )}
    </>
  );
};

export default Favs;
