import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import '../index.css'

// Simulación de un contexto de tema (debes implementar tu propio contexto según tus necesidades)
const ThemeContext = React.createContext();

const App = () => {
  // Estado para controlar el tema (dark o light)
  const [theme, setTheme] = useState('light');

  // Función para cambiar el tema
  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <ThemeContext.Provider value={theme}>
      <div className={`App ${theme}`}>
        <Navbar toggleTheme={toggleTheme} />
        {/* Configura tus rutas y componentes aquí */}
      </div>
    </ThemeContext.Provider>
  );
};

const Navbar = ({ toggleTheme }) => {
  const theme = useContext(ThemeContext);

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem',
    background: theme === 'light' ? 'white' : '#12121296',
    color: theme === 'light' ? 'black' : 'white',
  };

  const ulStyle = {
    listStyle: 'none',
    margin: 0,
    padding: 0,
    display: 'flex',
  };

  const liStyle = {
    marginRight: '20px',
  };

  return (
    <nav style={navbarStyle}>
      <ul style={ulStyle}>
        <li style={liStyle}>
          <Link to="/home">Home</Link>
        </li>
        <li style={liStyle}>
          <Link to="/contacto">Contacto</Link>
        </li>
        <li style={liStyle}>
          <Link to="/favs">Favoritos</Link>
        </li>
      </ul>
      <button onClick={toggleTheme}>Cambiar Tema</button>
    </nav>
  );
};

export default App;
