import React, { useEffect, useState } from 'react';
import Card from '../Components/Card';

const Home = () => {
  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    // ...

    const addToFavorites = (dentist) => {
      // Recupera los favoritos actuales del localStorage (si los hay)
      const favorites = JSON.parse(localStorage.getItem('favorites')) || [];

      // Verifica si el dentista ya está en favoritos por su ID
      if (!favorites.includes(dentist.id)) {
        // Si no está en favoritos, agrégalo
        favorites.push(dentist.id);

        // Actualiza el localStorage con los nuevos favoritos
        localStorage.setItem('favorites', JSON.stringify(favorites));

        // Además, guarda el dentista completo en localStorage por su ID
        localStorage.setItem(`dentist-${dentist.id}`, JSON.stringify(dentist));
      }
    };

    // ...
  }, []);

  // ...

  return (
    <main className="light">
      <h1>Home</h1>
      <div className="card-grid">
        {dentists.map((dentist) => (
          <Card
            key={dentist.id}
            dentist={dentist}
            addToFavorites={() => addToFavorites(dentist)}
          />
        ))}
      </div>
    </main>
  );
};

export default Home;
