import React from "react";

const Card = ({ name, username, id, addToFavorites }) => {

  const addFav = () => {
    // Lógica para agregar la Card en el localStorage
    addToFavorites(id);
  }

  return (
    <div className="card">
      <h2>{name}</h2>
      <p>Username: {username}</p>
      <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  );
};

export default Card;
