import React, { useEffect, useState } from "react";
import Card from "../Components/Card";

const Favs = () => {
  const [favorites, setFavorites] = useState([]);
  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    // Recupera los dentistas destacados del localStorage
    const storedFavorites = JSON.parse(localStorage.getItem("favorites")) || [];
    setFavorites(storedFavorites);

    // Recupera todos los dentistas (puedes obtenerlos de tu fuente de datos)
    // Por ahora, usaremos datos de ejemplo
    const exampleDentists = [
      { id: 1, name: "Dentista 1", username: "dentist1" },
      { id: 2, name: "Dentista 2", username: "dentist2" },
      { id: 3, name: "Dentista 3", username: "dentist3" },
    ];
    setDentists(exampleDentists);
  }, []);

  const removeFromFavorites = (id) => {
    // Elimina un dentista de los destacados
    const updatedFavorites = favorites.filter((favoriteId) => favoriteId !== id);
    setFavorites(updatedFavorites);
    // Actualiza el localStorage
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
  };

  const resetFavorites = () => {
    // Resetea todos los destacados
    setFavorites([]);
    localStorage.removeItem("favorites");
  };

  return (
    <>
      <h1>Dentists Favs</h1>
      <div className="card-grid">
        {dentists
          .filter((dentist) => favorites.includes(dentist.id))
          .map((dentist) => (
            <Card
              key={dentist.id}
              dentist={dentist}
              removeFromFavorites={() => removeFromFavorites(dentist.id)}
            />
          ))}
      </div>
      {favorites.length === 0 ? (
        <p>No tienes dentistas destacados.</p>
      ) : (
        <>
          <button onClick={resetFavorites}>Resetear Destacados</button>
        </>
      )}
    </>
  );
};

export default Favs;
