import React, { useEffect, useState } from 'react';
import Card from '../Components/Card';

const Home = () => {
  const [dentists, setDentists] = useState([]);
  const [favoriteDentists, setFavoriteDentists] = useState([]); // Nuevo estado para dentistas favoritos

  useEffect(() => {
    // Simulaci贸n de carga de datos desde una API (reemplaza con tu fuente de datos real)
    const fetchData = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        setDentists(data);
      } catch (error) {
        console.error('Error al obtener datos de los dentistas', error);
      }
    };

    fetchData();
  }, []);

  const addToFavorites = (dentist) => {
    setFavoriteDentists((prevFavorites) => [...prevFavorites, dentist]);
  };

  const removeFromFavorites = (dentistId) => {
    setFavoriteDentists((prevFavorites) => prevFavorites.filter((dentist) => dentist.id !== dentistId));
  };

  return (
    <main className="light">
      <h1>Home</h1>
      <h2>Dentistas destacados</h2>
      <div className="card-grid">
        {dentists.map((dentist) => (
          <Card
            key={dentist.id}
            dentist={dentist}
            addToFavorites={() => addToFavorites(dentist)}
            removeFromFavorites={() => removeFromFavorites(dentist.id)} // Pasar la funci贸n para eliminar
            isFavorite={favoriteDentists.some((favDentist) => favDentist.id === dentist.id)} // Comprobar si es favorito
          />
        ))}
      </div>
    </main>
  );
};

export default Home;
