import React, { useEffect, useState } from 'react';
import Card from '../Components/Card';

const Home = () => {
  const [dentists, setDentists] = useState([]);
  const [selectedDentist, setSelectedDentist] = useState(null);

  useEffect(() => {
    // Simulación de carga de datos desde una API (reemplaza con tu fuente de datos real)
    const fetchData = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        setDentists(data);
      } catch (error) {
        console.error('Error al obtener datos de los dentistas', error);
      }
    };

    fetchData();
  }, []);

  const addToFavorites = (dentist) => {
    setSelectedDentist(dentist);
  };

  return (
    <main className="light">
      <h1>Home</h1>
      <div className="card-grid">
        {dentists.map((dentist) => (
          <Card
            key={dentist.id}
            dentist={dentist}
            addToFavorites={() => addToFavorites(dentist)}
          />
        ))}
      </div>
    </main>
  );
};

export default Home;
